procedure.content:
DROP PROCEDURE IF EXISTS SP_NAME;
DELIMITER |
CREATE PROCEDURE SP_NAME( IN p_id INT, IN p_order_id VARCHAR(16), IN p_external_order_id VARCHAR(16), IN p_investment_system_id VARCHAR(16), IN p_order_type TINYINT, IN p_order_status TINYINT, IN p_instrument_id INT, IN p_symbol VARCHAR(16), IN p_tif TINYINT, IN p_side_code TINYINT, IN p_base_ccy CHAR(3), IN p_order_qty VARCHAR(30), IN p_open_qty VARCHAR(30), IN p_released_qty VARCHAR(30), IN p_executed_qty VARCHAR(30), IN p_limit_prc VARCHAR(30), IN p_stop_prc VARCHAR(30), IN p_mark_prc VARCHAR(30), IN p_average_prc VARCHAR(30), IN p_entry_prc VARCHAR(30), IN p_released_val VARCHAR(30), IN p_executed_val VARCHAR(30), IN p_added_datetime CHAR(20), IN p_added_by VARCHAR(16), IN p_updated_datetime CHAR(20), IN p_updated_by VARCHAR(16) ) BEGIN
CALL UPDATE_PARAMETER( 'DEALING_ENGINE', 'curr_order_id', p_id );
INSERT INTO orders ( order_id, external_order_id, investment_system_id, order_type, order_status, instrument_id, symbol, tif, side_code, base_ccy, order_qty, open_qty, released_qty, executed_qty, limit_prc, stop_prc, mark_prc, average_prc, entry_prc, released_val, executed_val, added_datetime, added_by, updated_datetime, updated_by ) VALUES ( p_order_id, p_external_order_id, p_investment_system_id, p_order_type, p_order_status, p_instrument_id, p_symbol, p_tif, p_side_code, p_base_ccy, p_order_qty, p_open_qty, p_released_qty, p_executed_qty, p_limit_prc, p_stop_prc, p_mark_prc, p_average_prc, p_entry_prc, p_released_val, p_executed_val, p_added_datetime, p_added_by, p_updated_datetime, p_updated_by );
END| DELIMITER ;
